
// #region Life Cycle

function initPlainHtmlConverter() {
	// NOTE needs to match plainhtmlconverter.cs version
	globals.IsConverter = true;
	globals.ContentHandle = '[CONVERTER]';

	if (!isRunningOnHost()) {
		document.head.getElementsByTagName('title')[0].innerText = 'Editor (converter)';
	}

	globals.quill = initQuill();
	initClipboard();
	getEditorElement().classList.add('ql-editor-converter');

	globals.IsConverterLoaded = true;
	setEditorIsLoaded(true);

	onInitComplete_ntf();
}

// #endregion Life Cycle

// #region Getters
function getEditorInlineStyleHtml() {
	let sup_guid = suppressTextChanged();

	// store original html
	let editor_elm = getEditorElement();
	let orig_html = editor_elm.outerHTML;

	// convert all relevant props to inline and get html
	let props = [
		//'background',
		'color',
		'direction',
		'font-family',
		'font-size',
		'font-stretch',
		'font-style',
		'text-decoration'
	];
	computedStyleToInlineStyle(editor_elm, true, props);
	let result = editor_elm.innerHTML;

	// restore original html
	editor_elm.outerHTML = orig_html;
	unsupressTextChanged(sup_guid);

	// remove any syntax ui
	let result_doc = globals.DomParser.parseFromString(result, 'text/html');
	result_doc.querySelectorAll('select.ql-ui').forEach(x => x.remove());

	// escape entities
	result = encodeHtmlSpecialEntitiesFromHtmlDoc(null, result_doc);
	// remove <br>
	result = convertHtmlLineBreaks(result_doc.body.innerHTML);
	return result;
}
// #endregion Getters

// #region Setters

// #endregion Setters

// #region State

function isPlainHtmlConverter() {
	return getEditorElement().classList.contains('ql-editor-converter');
}
// #endregion State

// #region Actions
function setConverterContent(formatType, dataStr, verifyText) {
	// NOTE this mimics quills onPaste
	let text = formatType == 'plaintext' ? dataStr : verifyText;
	let html = formatType == 'plaintext' ? null : cleanHtmlForFragmentMarkers(dataStr, verifyText);
	setEditorText('');
	let range = getDocAsRange();
	const formats = getFormatAtDocIdx(range.index);
	const pastedDelta = globals.quill.clipboard.convert({ text, html }, formats);
	const delta = new Quill.imports.delta()
		.retain(range.index)
		.delete(range.length)
		.concat(pastedDelta);
	globals.quill.updateContents(delta, Quill.sources.USER);
	if (formatType == 'plaintext') {
		return;
	}
	// pasting html always adds leading new line
	let data_starts_with_new_line = verifyText && verifyText.length > 0 && verifyText[0] == '\n';
	let conv_starts_with_new_line = getText({ index: 0, length: 1 }) == '\n';
	if (conv_starts_with_new_line && !data_starts_with_new_line) {
		deleteText({ index: 0, length: 1 }, Quill.sources.USER);
	}
	
	// remove trailing new line
	deleteText({ index: getDocLength() - 1, length: 1 }, Quill.sources.USER);

	if (formatType == 'vsrtf2html') {
		// treat visual studio rtf as code block
		formatDocRange(getDocAsRange(), { 'code-block': 'plain' }, 'user')
	}
	updateQuill();
}

